@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  title = 'fun-personal-website';

  constructor() { }

  ngOnInit() {
    // document.documentElement.style.setProperty('--gradientColor1', "#000000");
